export default function handleInfo(state = { currentSection: 'Starting Zip Code' }, action) {
  console.log('reducer state', state)
  switch(action.type) {
    case 'ADD_STARTING_ZIP_CODE':
      return {...state, startingZip: action.data.formValue};
    default:
      return state;
  }
}
