export default function handleInfo(state = {}, action) {
    console.log('reducer');
    switch(action.type) {
        case 'ADD_CUSTOMER_DATA_TO_STATE':
            let newState = {...state, ...action.customerInfo };
            console.log('newState', newState);
            return newState;
        default:
            return state;
    }
};
