import LocationForm from '../components/LocationForm';

export default class FormContainer extends React.Component {
    constructor() {
        super();
        this.state = {
            currentStep: 1,
            startingZip: '',
            destinationZip: '',
            elevator: '',
            numOfBedrooms: 0,
            parking: '',
            numOfFloors: 0,
            customerName: '',
            customerPhone: '',
            customerEmail: ''
        };
    }

    handleSubmit = () => {
        //update this.state to reflect data typed into form
    }

    render() {
        switch(this.state.currentStep) {
            case 1:
                return <LocationForm handleSubmit={this.handleSubmit} />
            default: 
                return <p>An error occurred.</p>
        }
    }
}
