export default function handleInfo(state = { currentStep: 1 }, action) {
    //debugger;
    switch(action.type) {
        case 'ADD_LOCATION_DATA':
            console.log('action', action)
            return {startingZip: action.startingZip, destinationZip: action.destinationZip, currentStep: 2}
        case 'ADD_BEDROOM_ELEVATOR_INFO':
            console.log('ADD BEDROOM ELEVATOR INFO action', action);
            return {...state, numOfBedrooms: action.numOfBedrooms, numOfFloors: action.numOfFloors, elevator: action.elevator, currentStep: 3};
        default:
            return state;
    }
};
