//has props "handleSubmit" and "currentSection"

import React from 'react';

export default class TextFieldForm extends React.Component {
    constructor() {
        super();

        this.state = {
            fieldValue: ''
        }
    }

    handleChange = e => {
        this.setState({ fieldValue: e.target.value })
    }

    handleSubmit = e => {
        e.preventDefault();
        let formData = {
            infoType: this.props.name,
            data: this.state.fieldValue
        };
        this.props.submitForm(formData);
     }

    render() {
        console.log('textfieldform props', this.props);
        return (
            <div className="formDiv">
                <form className="formDiv__form" onSubmit={e => this.handleSubmit(e)}>
                    <label className="formDiv__form--label">{this.props.name}</label>
                    <input type="text"
                        name={this.props.name}
                        onChange={e => this.handleChange(e)}
                        value={this.state.fieldValue} />
                    <input type="submit" className="formDiv__form--button" value="Next" />
                </form>
            </div>
        );
    }
}
