import React from 'react';

export default class CustomerContactForm extends React.Component {
    constructor() {
        super();
        this.state = {
            customerName: '',
            customerEmail: '',
            customerPhone: ''
        }
    }

    handleNameChange = e => {
        let name = e.target.value;
        this.setState({ customerName: name });
    }

    handleEmailChange = e => {
        let email = e.target.value;
        this.setState({ customerEmail: email });
    }

    handleSubmit = () => {
        console.log('submitted');
    }

    render() {
        return (
            <div className="formDiv">
                <form className="formDiv__form">
                    <label className="formDiv__form--label">
                        How can M.M.S. contact you?
                        <input type="text" name="customerName" value={this.state.customerName} placeholder="Name" onChange={e => this.handleChange(e)} />
                        <input type="text" name="customerEmail" value={this.state.customerEmail} placeholder="Email address" onChange={e => this.handleEmailChange(e)} />
                    </label>
                    <input type="submit" className="formDiv__form--button" value="Submit" />
                </form>
            </div>
        )
    }
}
