export default function handleInfo(state = { currentStep: 1 }, action) {
    console.log('reducer');
    switch(action.type) {
        case 'ADD_LOCATION_DATA':
            return {...state, startingZip: action.startingZip, destinationZip: action.destinationZip, currentStep: 2}
        default:
            return state;
    }
};
