export default function handleInfo(state = { currentSection: 'Starting Zip Code' }, action) {
  console.log('reducer state', state)
  switch(action.type) {
    case 'ADD_STARTING_ZIP_CODE':
      return {...state, startingZip: action.data, currentSection: 'Destination Zip Code'};
    case 'ADD_DESTINATION_ZIP_CODE':
      return {...state, destinationZip: action.data, currentSection: 'Number of Bedrooms'};
    case 'ADD_NUMBER_OF_BEDROOMS':
      debugger;
    default:
      console.log('an error occurred in the reducer')
      return state;
  };
}
