import React from 'react';
import { connect } from 'react-redux';

import TextFieldForm from './TextFieldForm';
import NumberForm from './NumberForm';
import submitForm from '../actions/submitForm';

class Form extends React.Component {
    constructor() {
        super();
        this.state = {
            formValue: ''
        }
    }

    render() {
        switch(this.props.currentSection){
            case 'Starting Zip Code':
            case 'Destination Zip Code':
            case 'Customer Name':
            case 'Customer Phone Number':
            case 'Customer Email':
                return <TextFieldForm name={this.props.currentSection} submitForm={this.props.submitForm}/>;
            case 'Number of Bedrooms':
                return <NumberForm name={this.props.currentSection} submitForm={this.props.submitForm} />;
            default:
                return <p> An error occurred.</p>;
        }
    }
}

const mapStateToProps = state => {
    return { currentSection: state.currentSection };
}

const mapDispatchToProps = dispatch => {
    return {
        submitForm: formData => dispatch(submitForm(formData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Form);
