const handleInfo = (state = { 
  currentSection: 'Starting Zip Code', 
  formSections: ['Starting Zip Code', 'Destination Zip Code', 'Number of Bedrooms', 'How many floors?', 'Is there an elevator?', 'Where will the moving truck be able to park?', 'How can Millennial Moving Solutions Contact You?'],
  currentIndex: 0
}, action) => {
  switch(action.type){
  case 'ADD_CUSTOMER_INFO':
    console.log('in reducer', action)
    console.log('state before changes', state)
    let nextSection = state.formSections[state.currentIndex + 1];
    let nextIndex = state.currentIndex + 1;
    let infoType = action.formData.infoType;
    let infoValue = action.formData.data;
    let newState = {...state, currentSection: nextSection, currentIndex: nextIndex, infoType: infoValue};
    console.log('newState', newState);
    return 'hello'
  default:
    return state;
  }
};

export default handleInfo;
