import React from 'react';
import { connect } from 'react-redux';

import submitForm from '../actions/submitForm';

class Form extends React.Component {
    constructor() {
        super();
        this.state = {
            formValue: ''
        }
    }

    handleSubmit = e => {
       e.preventDefault();
       let formData = {
           infoType: this.props.currentSection,
           data: this.state.formValue
       };
       console.log('formData in handleSubmit', formData);
       this.props.submitForm(formData);
    }

    renderInput = () => {
        switch(this.props.currentSection) {
            case 'Starting Zip Code':
            case 'Destination Zip Code':
                return (
                    <input type="text" 
                        name={this.props.currentSection} 
                        onChange={e => this.handleChange(e)} 
                        value={this.state.formValue} />
                );
            default:
                return <p>An error occurred.</p>
        }
    }

    render() {
        switch(this.props.currentSection) {
            case 'Starting Zip Code':
            case 'Destination Zip Code':
                return <TextInput currentSection={this.props.currentSection} handleSubmit={this.handleSubmit} />
        }
    }
