import React from 'react';
import { connect } from 'react-redux';

import TextField from './TextField';

import submitForm from '../actions/submitForm';

class Form extends React.Component {

    handleSubmit = e => {
        e.preventDefault();
        console.log('e', e);
    }

    render() {
        return (
            <div className="formDiv">
                <form className="formDiv__form">
                    <label className="formDiv__form--label">{this.props.currentSection}</label>
                        <input type="text" name={this.props.currentSection} value={this.state.formValue} onChange={e => this.handleChange(e)} />
                        <input type="submit" className="form__button" onSubmit={e => this.handleSubmit(e)} value="Next" />
                </form>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return { currentSection: state.currentSection }
};

const mapDispatchToProps = dispatch => {
    return {
        submitForm: formData => dispatch(submitForm(formData))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Form);
