export default function handleInfo(state = { currentStep: 1 }, action) {
    //debugger;
    switch(action.type) {
        case 'ADD_LOCATION_DATA':
            return {startingZip: action.startingZip, destinationZip: action.destinationZip, currentStep: state.currentStep+1}
        case 'ADD_BEDROOM_ELEVATOR_INFO':
            return {...state, numOfBedrooms: action.numOfBedrooms, numOfFloors: action.numOfFloors, elevator: action.elevator, currentStep: state.currentStep+1};
        case 'ADD_PARKING_INFO':
            let newState = {...state, parkingDistance: action.parkingDistance, currentStep: state.currentStep+1}
            console.log('newstate', newState);
        default:
            return state;
    }
};
