export default function handleInfo(state = { currentStep: 1 }, action) {
    //debugger;
    switch(action.type) {
        case 'ADD_LOCATION_DATA':
            console.log('action', action)
            return {startingZip: action.startingZip, destinationZip: action.destinationZip, currentStep: state.currentStep + 1}
        default:
            return state;
    }
};
